    private void submitActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_submitActionPerformed
    String uid=txtuid.getText();
    String pwd=txtpwd.getText();
    String pwd1=txtpwd1.getText();
    String u=null;

    if(uid.equals("Enter Username") || uid.isEmpty())
        JOptionPane.showMessageDialog(this,"Please Enter Username");
    else
    {
        try
        {
          callcon cc=new callcon();
          Statement stmt=cc.callcon().createStatement();
          String qry="SELECT * FROM login; ";
          ResultSet rs=stmt.executeQuery(qry);
          while(rs.next())
          {
              u=rs.getString(1);
          }
          rs.close();
          cc.callcon().close();
          stmt.close();
        }
        catch(Exception e1)
        {
          JOptionPane.showMessageDialog(null,e1.toString());
        }     
            if(pwd.equals(pwd1))
        {
            try
            {
                callcon cc=new callcon();
                Statement stmt=cc.callcon().createStatement();
                String qry="INSERT INTO login values('"+uid+"','"+pwd+"') ";
                stmt.executeUpdate(qry);
                cc.callcon().close();
                JOptionPane.showMessageDialog(this,"New User Created Successfully.");
                txtuid.setText("");
                txtpwd.setText("");
                txtpwd1.setText("");
                LoginForm lf= new LoginForm();
                lf.setVisible(true);
                this.setVisible(false);
            }
            catch(Exception e)
            {
                JOptionPane.showMessageDialog(this,e.toString());
            }

        }
        else
        {
          JOptionPane.showMessageDialog(this,"Password mis-match error!!");
        }
    }
    }//GEN-LAST:event_submitActionPerformed

