    private void txtuidCaretUpdate(javax.swing.event.CaretEvent evt) {//GEN-FIRST:event_txtuidCaretUpdate
    String uid=txtuid.getText();
    if(uid.isEmpty())
        lbluid.setText("Field Required");
    else
        lbluid.setText(null);
    }//GEN-LAST:event_txtuidCaretUpdate

    private void txtpwdCaretUpdate(javax.swing.event.CaretEvent evt) {//GEN-FIRST:event_txtpwdCaretUpdate
        String pwd=txtpwd.getText();
        if(pwd.length()<6)
            lblpwd.setText("Pwd must be in between 6-40");
        else
            lblpwd.setText(null);       
    }//GEN-LAST:event_txtpwdCaretUpdate

    private void txtpwd1CaretUpdate(javax.swing.event.CaretEvent evt) {//GEN-FIRST:event_txtpwd1CaretUpdate
    String pwd= new String(txtpwd.getText());
    String pwd1= new String(txtpwd1.getText());
    if(pwd.equals(pwd1)==false)
        lblpwd1.setText("Password mis-match");
    else
        lblpwd1.setText(null);
    }//GEN-LAST:event_txtpwd1CaretUpdate
    
