    private void loginActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_loginActionPerformed
     String u=txtuid.getText();
     String q=null,w=null;
     
          
    if(u.isEmpty())
    {
        JOptionPane.showMessageDialog(this,"Enter Username");
    }
    else
    {        
    try
     {
         callcon cc=new callcon();
         Statement stmt=cc.callcon().createStatement();
         String qry="SELECT * FROM login WHERE uid= '"+u+"' ";
         ResultSet rs=stmt.executeQuery(qry);

        while(rs.next())
        {
        q=rs.getString(1);
        w=rs.getString(2);
        }
     }
    catch( Exception e)
     {
         JOptionPane.showMessageDialog(this,e.toString());
     }
            
    try{   
            if(txtuid.getText().equals(q) && txtpwd.getText().equals(w))
            {
                JOptionPane.showMessageDialog(null, " Welcome to Aadhaar Record Keeping System");
                this.setVisible(false);
                MainForm m= new MainForm();
                m.setVisible(true);
                this.setVisible(false);
            }
            else
            {
            JOptionPane.showMessageDialog(null, "Invalid Login...");
            System.out.print("null");
            txtuid.setText(null);
            txtpwd.setText(null);
            txtuid.setBorder(BorderFactory.createLineBorder(Color.gray));
            txtpwd.setBorder(BorderFactory.createLineBorder(Color.gray));
            }
    }
    catch( Exception e){
        System.out.println(e);
    }
    } 
    }//GEN-LAST:event_loginActionPerformed

    private void cbpwdActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cbpwdActionPerformed
     if(cbpwd.isSelected()==true)
     {
         txtpwd.setEchoChar((char)0);
     }
     else
     {
         txtpwd.setEchoChar('*');
     }
}//GEN-LAST:event_cbpwdActionPerformed

