    private void txtuidCaretUpdate(javax.swing.event.CaretEvent evt) {//GEN-FIRST:event_txtuidCaretUpdate
    if(txtuid.getText().isEmpty())
        txtuid.setBorder(BorderFactory.createLineBorder(Color.red));
    else
        txtuid.setBorder(BorderFactory.createLineBorder(Color.gray));
    }//GEN-LAST:event_txtuidCaretUpdate

    private void txtpwdCaretUpdate(javax.swing.event.CaretEvent evt) {//GEN-FIRST:event_txtpwdCaretUpdate
    if(txtpwd.getText().isEmpty())
        txtpwd.setBorder(BorderFactory.createLineBorder(Color.red));
    else
        txtpwd.setBorder(BorderFactory.createLineBorder(Color.gray));
    }//GEN-LAST:event_txtpwdCaretUpdate

